export unload_query=" UNLOAD (' SELECT offer_nbr, CAST( TO_CHAR(store_nbr , ''FM0000'' ) AS VARCHAR(18)) AS store_nbr, dstrbtn_chnl_code , dstrbtn_chnl_desc, CAST(offer_start_date as VARCHAR(10) ) AS offer_start_date, CAST(offer_end_date as VARCHAR(10) ) AS offer_end_date, CAST(eff_date as VARCHAR(10) ) AS eff_date, row_status_ind, to_char(last_update_date, ''YYYY-MM-DD HH24:MI:SS'') AS last_update_date, to_char(insert_date, ''YYYY-MM-DD HH24:MI:SS'') AS insert_date FROM $SCHEMA.$TABLE_N where offer_nbr in ( Select offer_nbr FROM loyalty.offer Where alternate_src_sys_name <> ''CDA'') '   ) TO 's3://data-${S3_Source}-outbound/outbound/QUANTIUM/MASTER_DATA/OFFER_IN_LOCATION/OFFER_IN_LOCATION_${TS_Date}_' WITH CREDENTIALS 'aws_access_key_id=${AWS_ACCESS_KEY_ID};aws_secret_access_key=${AWS_SECRET_ACCESS_KEY}' DELIMITER '|' ALLOWOVERWRITE NULL AS '' PARALLEL ON GZIP manifest;"
